import React, { useState, useRef, useEffect } from 'react';

const PatternDraw = () => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const drawingData = useRef([]);
  const contextRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = 500;
    canvas.height = 500;

    const context = canvas.getContext('2d');
    context.strokeStyle = 'black';
    context.lineWidth = 2;
    contextRef.current = context;

    const startDrawing = (e) => {
      setIsDrawing(true);
      const x = e.clientX - canvas.getBoundingClientRect().left;
      const y = e.clientY - canvas.getBoundingClientRect().top;
      drawingData.current.push({ x, y });
      contextRef.current.beginPath();
      contextRef.current.moveTo(x, y);
    };

    const endDrawing = () => {
      setIsDrawing(false);
      contextRef.current.closePath();
    };

    const draw = (e) => {
      if (!isDrawing) return;
      const x = e.clientX - canvas.getBoundingClientRect().left;
      const y = e.clientY - canvas.getBoundingClientRect().top;
      drawingData.current.push({ x, y });
      contextRef.current.lineTo(x, y);
      contextRef.current.stroke();
    };

    canvas.addEventListener('mousedown', startDrawing);
    canvas.addEventListener('mouseup', endDrawing);
    canvas.addEventListener('mousemove', draw);

    return () => {
      canvas.removeEventListener('mousedown', startDrawing);
      canvas.removeEventListener('mouseup', endDrawing);
      canvas.removeEventListener('mousemove', draw);
    };
  }, [isDrawing]);

  useEffect(() => {
    // Redraw the existing drawing when the component re-renders
    const context = canvasRef.current.getContext('2d');
    context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    for (let i = 0; i < drawingData.current.length; i++) {
      if (i === 0) {
        context.beginPath();
        context.moveTo(drawingData.current[i].x, drawingData.current[i].y);
      } else {
        context.lineTo(drawingData.current[i].x, drawingData.current[i].y);
        context.stroke();
      }
    }

    context.closePath();
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{ border: '1px solid black' }}
    />
  );
};

export default DrawingCanvas;
