import React, { useState} from 'react';
import { Link } from 'react-router-dom';
import logo from './logo.svg';

import './App.css';

function Logo() {
  return (
    <div className="logo-container">
      <a href={window.location.href}>
        <img className="logo" src={logo} alt="Logo" />
      </a>
    </div>
  );
}

function TopLink({handleLinkHover , handleLinkLeave}) {
  return (
    <div className="top-link-container">
      <Link to="/about" className='top-link' onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
        work
      </Link>
    </div>
  );
}

function Footer() {
  return (
    <footer className="footer">
      &copy; 2023 Gonçalo Barroso
    </footer>
  );
}

export default function AboutPage() {
  const [hovered, setHovered] = useState(false);

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  return (
    <div className={`about ${hovered ? 'about-hovered' : ''}`}>
      <div className="logo-container">
        <img className='logo' src={logo} alt="Logo" />
      </div>
      <span className="top-link">
        <Link to="/" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
          fun
        </Link>
      </span>
      <footer className="footer">
        &copy; 2023 Gonçalo Barroso
      </footer>
    </div>
  );
}
