import React, { useEffect, useRef } from 'react';

const PatternGenerator = () => {
    const canvasRef = useRef(null);
  
    useEffect(() => {
      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');
  
      canvas.width = 100;
      canvas.height = 50;
  
      const image1 = new Image();
      const image2 = new Image();
      const imageUrls = ['pattern1.svg', 'pattern2.svg'];
  
      // Create a function to load and draw images
      function loadImageAndDraw(url, x, y, rotation) {
        const img = new Image();
        img.src = url;
        img.onload = () => {
          context.save();
          context.translate(x, y);
          context.rotate((rotation * Math.PI) / 180); // Convert degrees to radians
          context.drawImage(img, -5, -5, 10, 10); // Assuming your square images are 10x10 pixels
          context.restore();
        };
      }
  
      // Create random positions and rotations for the square images
      function randomizePositionsAndRotations() {
        const positions = [];
        const rotations = [];
        for (let i = 0; i < 2; i++) {
          const x = Math.floor(Math.random() * 100);
          const y = Math.floor(Math.random() * 50);
          const rotation = Math.floor(Math.random() * 4) * 90; // Random rotation in 90-degree increments
          positions.push({ x, y });
          rotations.push(rotation);
        }
        return { positions, rotations };
      }
  
      const { positions, rotations } = randomizePositionsAndRotations();
  
      // Load and draw square images with random positions and rotations
      loadImageAndDraw(imageUrls[0], positions[0].x, positions[0].y, rotations[0]);
      loadImageAndDraw(imageUrls[1], positions[1].x, positions[1].y, rotations[1]);
    }, []);
  
    return <canvas ref={canvasRef}></canvas>;
  };
