import { useOnDraw } from './Hooks';
import { useState, useEffect } from 'react';

import CustomPatternGenerator from './Component/PatternDraw';

const PatternDraw = ({ width, height }) => {
    const { setCanvasRef, onCanvasMouseDown } = useOnDraw(onDraw);
    const [currentColor, setCurrentColor] = useState('#FF0000');
    const [drawings, setDrawings] = useState([]); // State to store drawings

    useEffect(() => {
        clearCanvas();
    }, []);

    function onDraw(ctx, point, prevPoint) {
        drawLine(prevPoint, point, ctx, currentColor, 30);
    }

    function drawLine(start, end, ctx, color, width) {
        start = start ?? end;
        ctx.beginPath();
        ctx.lineWidth = width;
        ctx.strokeStyle = color;
        ctx.moveTo(start.x, start.y);
        ctx.lineTo(end.x, end.y);
        ctx.stroke();

        ctx.fillStyle = color;
        ctx.beginPath();
        ctx.arc(start.x, start.y, 2, 0, 2 * Math.PI);
        ctx.fill();
    }

    function clearCanvas() {
        const canvas = document.querySelector('canvas');
        const ctx = canvas.getContext('2d');
        ctx.fillStyle = '#ffffff'; // Set to white
        ctx.fillRect(0, 0, canvas.width, canvas.height);
    }

    function changeColor(newColor) {
        setCurrentColor(newColor);
    }

    function saveDrawing() {
        const canvas = document.querySelector('canvas'); // Get the canvas element

        if (canvas) {
            const svgData = canvas.toDataURL('image/svg+xml');
            const updatedDrawings = [...drawings, svgData];

            if (updatedDrawings.length > 2) {
                // If there are more than 2 drawings, remove the oldest one
                updatedDrawings.shift();
            }

            setDrawings(updatedDrawings);
            clearCanvas();
        }
    }

    function clearAllDrawings() {
        setDrawings([]);
    }

    return (
        <div style={{ display: 'flex' }}>
            <div>
                <canvas
                    width={width}
                    height={height}
                    onMouseDown={onCanvasMouseDown}
                    ref={setCanvasRef}
                />
                <button className="canvas-button" onClick={clearCanvas}>
                    limpar
                </button>
                <button className="canvas-button" onClick={saveDrawing}>
                    adicionar
                </button>
                <button className="canvas-button" onClick={clearAllDrawings}>
                    apagar todos
                </button>
                <button className="canvas-button" onClick={() => changeColor('#FF0000')}>
                    vermelho
                </button>
                <button className="canvas-button" onClick={() => changeColor('#00FF00')}>
                    verde
                </button>
                <button className="canvas-button" onClick={() => changeColor('#0000FF')}>
                    azul
                </button>
                <button className="canvas-button" onClick={() => changeColor('#000000')}>
                    preto
                </button>
            </div>
            <div>
                {drawings.map((drawing, index) => (
                    <img
                        className='saved-patterns'
                        key={index}
                        src={drawing}
                        alt={`Saved Drawing ${index}`}
                    />
                ))}
            </div>
        </div>
    );
}

export default PatternDraw;
