import React, { useEffect, useRef, useState } from 'react';
import '../App.css';

const CustomPatternGenerator = ({ imageArray }) => {
  const canvasRef = useRef(null);
  const [folderNumber, setFolderNumber] = useState(1); // Start with folderNumber 1

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const numRows = 15;
    const numCols = 30;
    const squareSize = 75;

    canvas.width = numCols * squareSize;
    canvas.height = numRows * squareSize;

    function drawSquareImage(x, y, size, rotation, imageUrl) {
      const img = new Image();
      img.src = imageUrl;
      img.onload = () => {
        context.save();
        context.translate(x, y);
        context.rotate((rotation * Math.PI) / 180);
        context.drawImage(img, -size / 2, -size / 2, size, size);
        context.restore();
      };
    }

    for (let row = 0; row < numRows; row++) {
      for (let col = 0; col < numCols; col++) {
        const x = col * squareSize + squareSize / 2;
        const y = row * squareSize + squareSize / 2;
        const rotation = Math.floor(Math.random() * 4) * 90;
        const randomValue = Math.random();

        const imageUrl =
          imageArray && imageArray.length === 2
            ? imageArray[Math.floor(randomValue * imageArray.length)]
            : getDefaultImageUrl(randomValue, folderNumber);

        drawSquareImage(x, y, squareSize, rotation, imageUrl);
      }
    }

    // Update folderNumber sequentially (loop from 1 to 4)
    setFolderNumber((prevFolderNumber) => (prevFolderNumber % 4) + 1);
  }, [imageArray, folderNumber]);

  function getDefaultImageUrl(randomValue, folderNumber) {
    const defaultImageUrls = [
      `./pattern${folderNumber}/pattern1.png`,
      `./pattern${folderNumber}/pattern2.png`,
      `./pattern${folderNumber}/pattern3.png`,
      `./pattern${folderNumber}/pattern4.png`,
    ];

    if (randomValue < 0.25) {
      return defaultImageUrls[0];
    } else if (randomValue < 0.50) {
      return defaultImageUrls[1];
    } else if (randomValue < 0.75) {
      return defaultImageUrls[2];
    } else {
      return defaultImageUrls[3];
    }
  }

  return <canvas ref={canvasRef} className='background'></canvas>;
};

export default CustomPatternGenerator;
