import {useOnDraw} from './Hooks';
import { useState, useRef } from 'react';

const PatternDraw = ({ width,height }) => {

    const {setCanvasRef,onCanvasMouseDown} = useOnDraw(onDraw);
    const [currentColor, setCurrentColor] = useState('#000000');
    const [drawings, setDrawings] = useState([]); // State to store drawings
    const canvasRef = useRef(null);

    function onDraw(ctx, point, prevPoint) {
        drawLine(prevPoint, point, ctx, currentColor, 30);
    }

    function drawLine(
        start,
        end,
        ctx,
        color,
        width
    ) {
        start = start ?? end;
        ctx.beginPath();
        ctx.lineWidth = width;
        ctx.strokeStyle = color;
        ctx.moveTo(start.x, start.y);
        ctx.lineTo(end.x, end.y);
        ctx.stroke();

        ctx.fillStyle = color;
        ctx.beginPath();
        ctx.arc(start.x, start.y, 2, 0, 2 * Math.PI);
        ctx.fill();

    }

    function clearCanvas() {
        const canvas = document.querySelector('canvas'); // Get the canvas element
        const ctx = canvas.getContext('2d'); // Get the 2D rendering context
    
        // Clear the entire canvas
        ctx.clearRect(0, 0, canvas.width, canvas.height);
    }

    function changeColor(newColor) {
        setCurrentColor(newColor);
    }

    function saveDrawing() {
        const canvas = canvasRef.current;
        const svgData = canvas.toDataURL("image/svg+xml");
        setDrawings([...drawings, svgData]);
        clearCanvas(); // Clear the canvas after saving
    }

    return(
        <div>
        <canvas
            width={width}
            height={height}
            onMouseDown={onCanvasMouseDown}
            ref={setCanvasRef}
        />
        <button className="canvas-button" onClick={clearCanvas}>limpar</button>
        <button className="canvas-button" onClick={saveDrawing}>adicionar</button>
        <button className="canvas-button" onClick={() => changeColor('#FF0000')}>vermelho</button>
        <button className="canvas-button" onClick={() => changeColor('#00FF00')}>verde</button>
        <button className="canvas-button" onClick={() => changeColor('#0000FF')}>azul</button>
        <button className="canvas-button" onClick={() => changeColor('#000000')}>preto</button>
        </div>
    );

}

export default PatternDraw;
