import React, { useState, useEffect } from 'react';
import CustomPatternGenerator from './Component/CustomPatternGenerator';
import './App.css';
import Logo from './Component/Logo';
import TopLink from './Component/TopLink';
import Footer from './Component/Footer';

export default function HomePage() {
  const [elementsVisible, setElementsVisible] = useState(false);
  const [refreshCount, setRefreshCount] = useState(
    parseInt(localStorage.getItem('refreshCount')) || 0
  );
  const savedDrawings = JSON.parse(localStorage.getItem('drawings')) || [];
  let prevCount;

  useEffect(() => {
    // Increment the refresh count
    setRefreshCount((prevCount) => prevCount + 1);

    // Update the local storage with the new refresh count
    localStorage.setItem('refreshCount', prevCount + 1);

    console.log(`Homepage refreshed! Count: ${refreshCount + 1}`);

    const delay = setTimeout(() => {
      setElementsVisible(true);
    }, 500); // Adjust ms it takes to load

    return () => {
      clearTimeout(delay); // Clear the timeout if the component unmounts
    };
  }, [prevCount, ]); // Empty dependency array to run the effect only once on mount

  return (
    <div className='home'>
      <div className={`fade-in ${elementsVisible ? 'visible' : ''}`}>
        <CustomPatternGenerator imageArray={savedDrawings} />
        <Logo />
        <TopLink />
        <Footer />
      </div>
      <div className='background-home'></div>
    </div>
  );
}
