import {useOnDraw} from './Hooks';

const PatternDraw = ({
    width,
    height
}) => {

    const {
        setCanvasRef,
        onCanvasMouseDown
    } = useOnDraw(onDraw);

    function onDraw(ctx, point, prevPoint) {
        drawLine(prevPoint, point, ctx, '#000000', 30);
    }

    function drawLine(
        start,
        end,
        ctx,
        color,
        width
    ) {
        start = start ?? end;
        ctx.beginPath();
        ctx.lineWidth = width;
        ctx.strokeStyle = color;
        ctx.moveTo(start.x, start.y);
        ctx.lineTo(end.x, end.y);
        ctx.stroke();

        ctx.fillStyle = color;
        ctx.beginPath();
        ctx.arc(start.x, start.y, 2, 0, 2 * Math.PI);
        ctx.fill();

    }

    function clearCanvas(){
        console.log("clear!")
    }

    return(
        <div>
        <canvas
            width={width}
            height={height}
            onMouseDown={onCanvasMouseDown}
            ref={setCanvasRef}
        />
        <button onClick={clearCanvas}>Clear</button>
        </div>
    );

}

export default PatternDraw;
