import React, { useEffect,  useState, useRef} from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import logo from './logo.svg';
import apple0 from './apple/apple0.svg'

import './App.css';

export default function HomePage() {
  const [hovered, setHovered] = useState(false);
  const [mousePosition, setMousePosition] = useState({x:0, y:0});
  const canvasRef = useRef(null);

  const handleClick = (e) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const x = e.clientX - canvas.getBoundingClientRect().left;
    const y = e.clientY - canvas.getBoundingClientRect().top;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(apple0, 0, 0, canvas.width, canvas.height);

    const pixelData = ctx.getImageData(x, y, 1, 1).data;

    // Check the pixel color to determine if it's transparent or not
    if (pixelData[3] !== 0) {
      // The click is on a non-transparent part of the image
      console.log('Image clicked!');
    }
  };

  useEffect(() =>{
    const mouseMove = e =>{
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
    }

    window.addEventListener("mousemove", mouseMove);

    return () => {
      window.removeEventListener("mousemove", mouseMove);
    }
  }, [])

  const variants = {
    default: {
      x: mousePosition.x -16,
      y: mousePosition.y -16,
      transition: {
        type: 'spring',
        stiffness: 5000,
        damping: 100,
      },
    }
  }

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <div className="logo-container">
        <img className='logo' src={logo} alt="Logo" />
      </div>
      <span className="top-link" >      
        <Link to="/about" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
          work
        </Link>
      </span>
      <footer className="footer">
        &copy; 2023 Gon√ßalo Barroso
      </footer>
      <motion.div 
        className="cursor" 
        variants={variants} 
        animate="default"
      />
      <div className="object-container">
        <canvas
          ref={canvasRef}
          width='20'
          height="height_of_your_image"
          style={{ display: 'none' }} // Hide the canvas
        />
        <img
          className='apple'
          src={apple0}
          alt="Logo"
          onClick={handleClick}
        />
      </div>
    </div>
  );
}
