import React, { useState} from 'react';
import { Link } from 'react-router-dom';

import logo from './logo.svg';

import './App.css';

export default function HomePage() {
  const [hovered, setHovered] = useState(false);
  const [object, setObject] = useState(0);
 
  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  const handleClick = () => {
    setObject(object+1);
    console.log(object);
  }

  const data = [
    {
      key: "1",
      img: "1.jpg"
    },
    {
      key: "2",
      img: "2.jpg"
    }
  ];

  const getImageSource = (object) => {
    return require('./objects/apple${object%2}.svg').default;
  };

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <div className="logo-container">
        <img className='logo' src={logo} alt="Logo" />
      </div>
      <span className="top-link" >      
        <Link to="/about" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
          work
        </Link>
      </span>
      <footer className="footer">
        &copy; 2023 Gon√ßalo Barroso
      </footer>
      <div className="object-container">
        <button className='object-button'>
          <img 
            className='apple' 
            src={getImageSource(object)}
            alt="apple" 
            onClick={handleClick} 
          />
          </button>
      </div>
    </div>
  );
}
