import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import logo from './logo.svg';

import './App.css';

export default function HomePage() {
  const [hovered, setHovered] = useState(false);

  const handleLinkHover = () => {
    setHovered(true);
    console.log("hovered");
  };

  const handleLinkLeave = () => {
    setHovered(false);
    console.log("not hovered");
  };

  useEffect(() => {
    const handleScroll = (event) => {
      const logoContainer = logoContainerRef.current;
      if (logoContainer) {
        const scrollSpeed = 100; // Adjust the scroll speed factor as needed
        const deltaY = event.deltaY;
        const currentAnimation = logoContainer.style.animation;
        const currentDuration = parseFloat(currentAnimation.split(" ")[1]);
        const newDuration = currentDuration + deltaY * scrollSpeed;
        logoContainer.style.animation = `rotate ${newDuration}s linear infinite`;
      }
    };

    window.addEventListener('wheel', handleScroll);

    return () => {
      window.removeEventListener('wheel', handleScroll);
    };
  }, []);

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <div className="logo-container" ref={logoContainerRef}>
        <img className='logo' src={logo} alt="Logo" />
      </div>
      <span className="top-link" >      
        <Link to="/about" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
          work
        </Link>
      </span>
    </div>
  );
}
