import React, { useEffect, useRef } from 'react';
import '../App.css';

const PatternGenerator = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const numRows = 15; // Number of rows
    const numCols = 30; // Number of columns
    const squareSize = 75; // Size of each square

    canvas.width = numCols * squareSize;
    canvas.height = numRows * squareSize;

    // Array of image URLs
    const imageUrls = [
      'pattern1.png', // Replace with the actual URL for your first square image
      'pattern2.png', // Replace with the actual URL for your second square image
      'pattern3.png', // Replace with the actual URL for your third square image
      'pattern4.png', // Replace with the actual URL for your fourth square image
    ];

    // Function to draw a square image with a specific rotation
    function drawSquareImage(x, y, size, rotation, imageUrl) {
      const img = new Image();
      img.src = imageUrl;
      img.onload = () => {
        context.save();
        context.translate(x, y);
        context.rotate((rotation * Math.PI) / 180); // Convert degrees to radians
        context.drawImage(img, -size / 2, -size / 2, size, size);
        context.restore();
      };
    }

    // Generate the grid of squares with random rotations and images
    for (let row = 0; row < numRows; row++) {
      for (let col = 0; col < numCols; col++) {
        const x = col * squareSize + squareSize / 2;
        const y = row * squareSize + squareSize / 2;
        const rotation = Math.floor(Math.random() * 4) * 90; // Random rotation in 90-degree increments
        const randomValue = Math.random(); // Random value between 0 and 1
        let imageUrl;

        // Adjust the probabilities as needed
        const rand=randomValue;
        if (rand < 0.25) {
          imageUrl = imageUrls[0]; // 25% probability for pattern1
        } else if (rand < 0.50) {
          imageUrl = imageUrls[1]; // 25% probability for pattern2
        } else if (rand < 0.75) {
          imageUrl = imageUrls[2]; // 6.25% probability for pattern3
        } else {
          imageUrl = imageUrls[3]; // 6.25% probability for pattern4
        }

        drawSquareImage(x, y, squareSize, rotation, imageUrl);
      }
    }
  }, []);

  return <canvas ref={canvasRef} className='background'></canvas>;
};

export default PatternGenerator;
