import React, { useEffect, useRef, memo } from 'react';
import '../App.css';

const CustomPatternGenerator = memo(({ imageArray }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const numRows = 15;
    const numCols = 30;
    const squareSize = 75;

    canvas.width = numCols * squareSize;
    canvas.height = numRows * squareSize;

    function drawSquareImage(x, y, size, rotation, imageUrl) {
      const img = new Image();
      img.src = imageUrl;
      img.onload = () => {
        context.save();
        context.translate(x, y);
        context.rotate((rotation * Math.PI) / 180);
        context.drawImage(img, -size / 2, -size / 2, size, size);
        context.restore();
      };
    }

    for (let row = 0; row < numRows; row++) {
      for (let col = 0; col < numCols; col++) {
        const x = col * squareSize + squareSize / 2;
        const y = row * squareSize + squareSize / 2;
        const rotation = Math.floor(Math.random() * 4) * 90;
        const randomValue = Math.random();
        
        const imageUrl = getImageUrl(randomValue);

        drawSquareImage(x, y, squareSize, rotation, imageUrl);
      }
    }

    function getImageUrl(randomValue) {
      const defaultImageUrls = [
        'pattern1.png',
        'pattern2.png',
        'pattern3.png',
        'pattern4.png',
      ];

      if (imageArray && imageArray.length === 2) {
        return imageArray[Math.floor(randomValue * imageArray.length)];
      } else {
        return getDefaultImageUrl(randomValue);
      }
    }

    function getDefaultImageUrl(randomValue) {
      const defaultImageUrls = [
        'pattern1.png',
        'pattern2.png',
        'pattern3.png',
        'pattern4.png',
      ];

      if (randomValue < 0.25) {
        return defaultImageUrls[0];
      } else if (randomValue < 0.50) {
        return defaultImageUrls[1];
      } else if (randomValue < 0.75) {
        return defaultImageUrls[2];
      } else {
        return defaultImageUrls[3];
      }
    }
  }, [imageArray]);

  return <canvas ref={canvasRef} className='background'></canvas>;
});

export default CustomPatternGenerator;
