import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PatternGenerator from './Component/PatternGenerator';

import './App.css';
import PatternDraw from './Component/PatternDraw';
import Logo from './Component/Logo';

function TopLink({handleLinkHover , handleLinkLeave}) {
  return (
    <div className="top-link-container">
        <Link to="/" className='top-link' onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
            brincar
        </Link>
        <Link to="/trabalho" className='top-link' onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
            trabalhar
        </Link>
        <Link to="/criar" className='top-link' onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
            criar
        </Link>
    </div>  
  );
}

function Footer() {
  return (
    <footer className="footer">
      &copy; 2023 Gon√ßalo Barroso
    </footer>
  );
}

export default function PatternPage() {
  const [hovered, setHovered] = useState(false);
  const [object, setObject] = useState(0);
  const [elementsVisible, setElementsVisible] = useState(false);

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  useEffect(() => {
    const delay = setTimeout(() => {
      setElementsVisible(true); // After 3 seconds, set elements to be visible
    }, 500);

    return () => {
      clearTimeout(delay); // Clear the timeout if the component unmounts
    };
  }, []);

  return (
    <div className={`criar ${hovered ? 'criar-hovered' : 'criar'}`}>
        <div className={`fade-in ${elementsVisible ? 'visible' : ''}`}>
            <div className="custom-pattern">
                <PatternDraw 
                  width={500}
                  height={500}
                />
            </div>
            <Logo />
            <TopLink handleLinkHover={handleLinkHover} handleLinkLeave={handleLinkLeave}/>
            <Footer />
        </div>
    </div>
  );
}
