import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PatternGenerator from './Component/PatternGenerator';

import logo from './logo.svg';
import './App.css';

function Logo() {
  return (
    <div className="logo-container">
      <a href={window.location.href}>
        <img className="logo" src={logo} alt="Logo" />
      </a>
    </div>
  );
}
function TopLink({handleLinkHover , handleLinkLeave}) {
  return (
    <span className='top-link'>
      <Link to="/about" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
        work
      </Link>
    </span>
  );
}

function Footer() {
  return (
    <footer className="footer">
      &copy; 2023 Gon√ßalo Barroso
    </footer>
  );
}

function ObjectImage({object, handleClick}) {
  return (
    <div className="object-container">
      <button className="object-button">
        <img
          className="object"
          src={`${object % 4}.svg`}
          alt="object"
          onClick={handleClick}
        />
      </button>
    </div>
  );
}

export default function HomePage() {
  const [hovered, setHovered] = useState(false);
  const [object, setObject] = useState(0);
  const [patternLoaded, setPatternLoaded] = useState(false);
  const [showOtherElements, setShowOtherElements] = useState(false);

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  const handleClick = () => {
    setObject(object + 1);
  }

  useEffect(() => {
    // Simulate loading time with a setTimeout (replace this with your actual loading logic)
    setTimeout(() => {
      setPatternLoaded(true);
      // After PatternGenerator is loaded, trigger the visibility of other elements
      setTimeout(() => {
        setShowOtherElements(true);
      }, 500); // Delay for 0.5 seconds (500 milliseconds)
    }, 2000); // Adjust the time as needed
  }, []);

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <PatternGenerator />
      <div className={`other-elements ${showOtherElements ? 'visible' : 'invisible'}`}>
        {/*<ObjectImage object={object} handleClick={handleClick} />*/}
        <Logo />
        <TopLink handleLinkHover={handleLinkHover} handleLinkLeave={handleLinkLeave} />
        <Footer />
      </div>
    </div>
  );
}
