import React, { useEffect,  useState} from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import logo from './logo.svg';

import './App.css';

export default function HomePage() {
  const [hovered, setHovered] = useState(false);
  const [mousePosition, setMousePosition] = useState({x:0, y:0});
  const logoRef = useRef(null);

  useEffect(() =>{
    const mouseMove = e =>{
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
    }

    window.addEventListener("mousemove", mouseMove);

    return () => {
      window.removeEventListener("mousemove", mouseMove);
    }
  }, [])

  useEffect(() => {
    if (logoRef.current) {
      logoRef.current.addEventListener('dragstart', (e) => {
        e.preventDefault(); // Prevent drag behavior
      });
    }
  }, [logoRef]);

  const variants = {
    default: {
      x: mousePosition.x -20,
      y: mousePosition.y,
      transition: {
        type: 'spring',
        stiffness: 5000,
        damping: 100,
      },
    }
  }

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <div className="logo-container">
        <img className='logo' src={logo} alt="Logo" ref={logoRef}/>
      </div>
      <span className="top-link" >      
        <Link to="/about" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
          work
        </Link>
      </span>
      <footer className="footer">
        &copy; 2023 Gon√ßalo Barroso
      </footer>
      <motion.div 
        className="cursor" 
        variants={variants} 
        animate="default"
      />

    </div>
  );
}
