import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PatternGenerator from './Component/PatternGenerator';

import logo from './logo.svg';
import './App.css';

function Logo() {
  return (
    <div className="logo-container">
      <a href={window.location.href}>
        <img className="logo" src={logo} alt="Logo" />
      </a>
    </div>
  );
}

function TopLink({handleLinkHover , handleLinkLeave}) {
  return (
    <div className="top-link-container">
      <Link to="/about" className='top-link' onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
        trabalho
      </Link>
      <Link to="/about" className='top-link' onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
         criar padrão
      </Link>
    </div>
  );
}

function Footer() {
  return (
    <footer className="footer">
      &copy; 2023 Gonçalo Barroso
    </footer>
  );
}

export default function HomePage() {
  const [hovered, setHovered] = useState(false);
  const [object, setObject] = useState(0);
  const [elementsVisible, setElementsVisible] = useState(false);

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  useEffect(() => {
    const delay = setTimeout(() => {
      setElementsVisible(true); // After 3 seconds, set elements to be visible
    }, 500);

    return () => {
      clearTimeout(delay); // Clear the timeout if the component unmounts
    };
  }, []);

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <PatternGenerator /> 
      <div className={`fade-in ${elementsVisible ? 'visible' : ''}`}>
        <Logo />
        <TopLink handleLinkHover={handleLinkHover} handleLinkLeave={handleLinkLeave}/>
        <Footer />
        {/*<ObjectImage object={object} handleClick={handleClick}/>*/}
      </div>
    </div>
  );
}
