import React, { useEffect,  useState} from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import logo from './logo.svg';
import apple0 from './apple/apple0.svg'

import './App.css';

export default function HomePage() {
  const [hovered, setHovered] = useState(false);
  const [mousePosition, setMousePosition] = useState({x:0, y:0});

  useEffect(() =>{
    const mouseMove = e =>{
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
    }

    window.addEventListener("mousemove", mouseMove);

    return () => {
      window.removeEventListener("mousemove", mouseMove);
    }
  }, [])

  const handleLinkHover = () => {
    setHovered(true);
  };

  const handleLinkLeave = () => {
    setHovered(false);
  };

  const myfunction = () => {
    console.log("CLICKED");
  }

  return (
    <div className={`home ${hovered ? 'home-hovered' : 'home'}`}>
      <div className="logo-container">
        <img className='logo' src={logo} alt="Logo" />
      </div>
      <span className="top-link" >      
        <Link to="/about" onMouseEnter={handleLinkHover} onMouseLeave={handleLinkLeave}>
          work
        </Link>
      </span>
      <footer className="footer">
        &copy; 2023 Gon√ßalo Barroso
      </footer>
      <div className="object-container">
        <button className='object-button'><img className='apple' src={apple0} alt="apple" onClick={myfunction} /></button>
      </div>
    </div>
  );
}
