import React, { useState, useEffect } from 'react';
import CustomPatternGenerator from './Component/CustomPatternGenerator';

import './App.css';
import Logo from './Component/Logo';
import TopLink from './Component/TopLink';
import Footer from './Component/Footer';

export default function HomePage() {
  const [elementsVisible, setElementsVisible] = useState(false);
  const savedDrawings = JSON.parse(localStorage.getItem('drawings')) || [];
  const numberOfFolders = 3;
  const folderNumber = Math.floor(Math.random() * numberOfFolders) + 1;
  

  useEffect(() => {
    const delay = setTimeout(() => {
      setElementsVisible(true); 
    }, 250); //Adjust ms it takes to load

    return () => {
      clearTimeout(delay); // Clear the timeout if the component unmounts
    };
  }, []);

  return (
    <div className='home'>
      <div className={`fade-in ${elementsVisible ? 'visible' : ''}`}>
        <CustomPatternGenerator imageArray={savedDrawings} folderNumber={folderNumber}/> 
        <Logo />
        <TopLink/>
        <Footer/ >
      </div>
      <div className='background-home'></div>
    </div>
  );
}
